INSERT INTO `product_translations` (`id`, `product_id`, `locale`, `name`, `description`, `short_description`) VALUES
(70, 70, 'ar', 'Apple iPhone XS Max, 64GB -white', '<div id=\"dpx-product-description_feature_div\">\r\n<div id=\"descriptionAndDetails\" class=\"a-section a-spacing-extra-large\">\r\n<div id=\"productDescription_feature_div\" class=\"celwidget\" data-feature-name=\"productDescription\" data-cel-widget=\"productDescription_feature_div\">\r\n<div id=\"productDescription_feature_div\" class=\"a-row feature\" data-feature-name=\"productDescription\" data-template-name=\"productDescription\" data-cel-widget=\"productDescription_feature_div\">\r\n<div id=\"productDescription\" class=\"a-section a-spacing-small\">\r\n<p><span class=\"a-list-item\">CARRIER - This phone is locked to Simple Mobile from Tracfone, which means this device can only be used on the Simple Mobile wireless network.</span></p>\r\n<p><span class=\"a-list-item\">PLANS - Simple Mobile offers a variety of coverage plans, including 30-Day Unlimited Talk, Text &amp; Data. No activation fees, no credit checks, and no hassles on a nationwide lightning-fast network. For more information or plan options, please visit the Simple Mobile website.</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span class=\"a-list-item\">ACTIVATION - You&rsquo;ll receive a Simple Mobile SIM kit with this iPhone. Follow the instructions to get service activated with the Simple Mobile plan of your choice.</span></p>\r\n<p><span class=\"a-list-item\">6.5-inch Super Retina display (OLED) with HDR</span></p>\r\n<p><span class=\"a-list-item\">IP68 dust and water resistant (maximum depth of 2 meters up to 30 minutes)</span></p>\r\n<p><span class=\"a-list-item\">12MP dual cameras with dual OIS and 7MP TrueDepth front camera&mdash;Portrait mode, Portrait Lighting, Depth Control, and Smart HDR</span></p>\r\n<p><span class=\"a-list-item\">Face ID for secure authentication and Apple Pay</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<div class=\"a-row a-expander-container a-expander-inline-container\" aria-live=\"polite\">\r\n<div class=\"a-expander-content a-expander-extend-content a-expander-content-expanded\" aria-expanded=\"true\">\r\n<p><span class=\"a-list-item\">A12 Bionic with next-generation Neural Engine</span></p>\r\n<p><span class=\"a-list-item\">Wireless charging&mdash;works with Qi chargers</span></p>\r\n<p><span class=\"a-list-item\">iOS 12 with Memoji, Screen Time, Siri Shortcuts, and Group FaceTime</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"text-decoration: underline;\"><strong><span class=\"a-list-item\">Specifications:</span></strong></span></p>\r\n<div class=\"a-column a-span6\">\r\n<table id=\"tech-specs-table-left\" class=\"a-bordered tech-spec-theme\">\r\n<tbody>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"text-align: left;\">\r\n<p>Display</p>\r\n</td>\r\n<td style=\"text-align: right;\">\r\n<p>6.5-inch Super Retina HD display with HDR and True Tone</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"text-align: left;\">\r\n<p>Capacity</p>\r\n</td>\r\n<td style=\"text-align: right;\">\r\n<p>64GB, 256GB, 512GB</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"text-align: left;\">\r\n<p>Splash, Water, and Dust Resistant</p>\r\n</td>\r\n<td style=\"text-align: right;\">\r\n<p>All-glass and stainless steel design, water and dust resistant (rated IP68)</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"text-align: left;\">\r\n<p>Camera &amp; Video</p>\r\n</td>\r\n<td style=\"text-align: right;\">\r\n<p>Dual 12MP cameras with Portrait mode, Depth Control, Portrait Lighting, Smart HDR, and 4K video up to 60 fps</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"text-align: left;\">\r\n<p>Front Camera</p>\r\n</td>\r\n<td style=\"text-align: right;\">\r\n<p>7MP TrueDepth front camera with Portrait mode, Depth Control, Portrait Lighting, and Smart HDR</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<div class=\"a-column a-span6 a-span-last\">\r\n<table id=\"tech-specs-table-right\" class=\"a-bordered tech-spec-theme\">\r\n<tbody>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"width: 131.667px;\">\r\n<p>Power and Battery</p>\r\n</td>\r\n<td style=\"width: 461.667px; text-align: right;\">\r\n<p>Talk time (wireless): Up to 25 hours; Internet use: Up to 13 hours; Video playback (wireless): Up to 15 hours; Audio playback (wireless): Up to 65 hours</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"width: 131.667px;\">\r\n<p>In the Box</p>\r\n</td>\r\n<td style=\"width: 461.667px; text-align: right;\">\r\n<p>iPhone with iOS 12, EarPods with Lightning Connector, Lightning to USB Cable, USB Power Adapter, Documentation</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"width: 131.667px;\">\r\n<p>Height</p>\r\n</td>\r\n<td style=\"width: 461.667px; text-align: right;\">\r\n<p>6.20 inches (157.5 mm)</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"width: 131.667px;\">\r\n<p>Width</p>\r\n</td>\r\n<td style=\"width: 461.667px; text-align: right;\">\r\n<p>3.05 inches (77.4 mm)</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"width: 131.667px;\">\r\n<p>Depth</p>\r\n</td>\r\n<td style=\"width: 461.667px; text-align: right;\">\r\n<p>0.30 inch (7.7 mm)</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class=\"a-text-bold\" style=\"width: 131.667px;\">\r\n<p>Weight</p>\r\n</td>\r\n<td style=\"width: 461.667px; text-align: right;\">\r\n<p>7.34 ounces (208 grams)</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div id=\"dpx-btf-renewed-program-description_feature_div\">\r\n<div id=\"renewedProgramDescriptionBtf_feature_div\" class=\"celwidget\" data-feature-name=\"renewedProgramDescriptionBtf\" data-cel-widget=\"renewedProgramDescriptionBtf_feature_div\"></div>\r\n</div>', '6.5-inch Super Retina display (OLED) with HDR\r\nIP68 dust and water resistant (maximum depth of 2 meters up to 30 minutes)\r\n12MP dual cameras with dual OIS and 7MP TrueDepth front camera—Portrait mode, Portrait Lighting, Depth Control, and Smart HDR'),
(71, 71, 'ar', 'Fossil Gen 5 Carlyle Stainless Steel Touchscreen Smartwatch', '<p><span class=\"a-list-item\">Smartwatches powered with Wear OS by Google work with iPhone and Android Phones ; Circumference: 200+/ 5mm</span></p>\r\n<p><span class=\"a-list-item\">Extend your battery life for multiple days with new, smart battery modes; magnetic USB rapid charger included; charge up to 80 percent in under an hour</span></p>\r\n<p>&nbsp;</p>\r\n<p><span class=\"a-list-item\"><img src=\"/storage/media/HTWqZMZOKcgm4DCeFctVRDF9iS5hFeBV6mNep3jP.jpeg\" alt=\"\" width=\"800\" height=\"450\" /></span></p>\r\n<p>&nbsp;</p>\r\n<p><span class=\"a-list-item\"><img src=\"/storage/media/KYl4fY1zsRvFFbWQr2JX0RgEh8G9d0AOgo20Fopg.jpeg\" alt=\"\" width=\"800\" height=\"450\" /></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span class=\"a-list-item\">Heart Rate &amp; Activity Tracking using Google Fit; Built in GPS for distance tracking; Swimproof design 3ATM; Google Assistant it&rsquo;s your own personal Google, always ready to help; Speed through checkout with your watch using Google Pay</span></p>\r\n<p><span class=\"a-list-item\">This is one smart watch now with a speaker for audible alerts and taking phone calls, Google Assistant responses and more; receive smartphone notifications and alerts; microphone; download third party apps with 8GB of storage and 1G RAM memory capacity</span></p>\r\n<p>&nbsp;</p>\r\n<p><span class=\"a-list-item\">Case size: 44mm; Band size: 22mm; interchangeable with all Fossil 22mm bands; Screen Size: 1.28\" display with 416x416 resolution; touchscreen functionality; Connectivity: Bluetooth(R) 4.1 Low Energy and Wi Fi 802.11 b/g/n; wireless syncing; imported</span></p>\r\n<p><span class=\"a-list-item\">Processor: Qualcomm Snapdragon Wear 3100</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span class=\"a-list-item\"><img src=\"/storage/media/pBeUAzU8rKCvCjawqEJLQuBiMVbXhfx5XcWB4K2v.jpeg\" alt=\"\" width=\"800\" height=\"300\" /></span></p>\r\n<p>&